mapper.update:
UPDATE pstg
SET pstg_inq_cnt = pstg_inq_cnt + 1 
WHERE pstg_no = #{pstgNo}

UPDATE pstg
SET pstg_wrt_title = #{pstgWrtTitle}, pstg_wrt_cn = #{pstgWrtCn}
WHERE pstg_no = #{pstgNo}

mapper.insert:
INSERT INTO pstg_img (
    pstg_img_pstg_no, 
    pstg_img_atch_no
)
VALUES (
    #{pstgNo}, 
    #{atchNo}
)
